	}

	public static bool IsIdentifierStart(char ch)
	{
		switch (ch)
		{
		default:
			if ((ch < 'a' || ch > 'z') && ch != '\\')
			{
				if (ch >= '\u0080')
				{
					return NonAsciiIdentifierStart.IsMatch(ParserExtensions.CharToString(ch));
				}
				return false;
			}
			break;
		case '$':
		case 'A':
		case 'B':
		case 'C':
		case 'D':
		case 'E':
		case 'F':
		case 'G':
		case 'H':
		case 'I':
		case 'J':
		case 'K':
		case 'L':
		case 'M':
		case 'N':
		case 'O':
		case 'P':
		case 'Q':
		case 'R':
		case 'S':
		case 'T':
		case 'U':
		case 'V':
		case 'W':
		case 'X':
		case 'Y':
		case 'Z':
		case '_':
			break;
		}
		return true;
	}

	public static bool IsIdentifierPart(char ch)
	{
		switch (ch)
		{
		default:
			if ((ch < 'a' || ch > 'z') && (ch < '0' || ch > '9') && ch != '\\')
			{
				if (ch >= '\u0080')
				{
					return NonAsciiIdentifierPart.IsMatch(ParserExtensions.CharToString(ch));
				}
				return false;
			}
			break;
		case '$':
		case 'A':
		case 'B':
		case 'C':
		case 'D':
		case 'E':
		case 'F':
		case 'G':
		case 'H':
		case 'I':
		case 'J':
		case 'K':
		case 'L':
		case 'M':
		case 'N':
		case 'O':
		case 'P':
		case 'Q':
		case 'R':
		case 'S':
		case 'T':
		case 'U':
		case 'V':
		case 'W':
		case 'X':
		case 'Y':
		case 'Z':
		case '_':
			break;
		}
		return true;
	}

	public static bool IsIdentifierStart(string ch)
	{
		if (ch.Length == 1)
		{
			return IsIdentifierStart(ch[0]);
		}
		return NonAsciiIdentifierStart.IsMatch(ch);
	}

	public static bool IsIdentifierPart(string ch)
	{
		if (ch.Length == 1)
		{
			return IsIdentifierPart(ch[0]);
		}
		return NonAsciiIdentifierPart.IsMatch(ch);
	}

	public static bool IsDecimalDigit(char cp)
	{
		if (cp >= '0')
		{
			return cp <= '9';
		}
		return false;
	}

	public static bool IsHexDigit(char cp)
	{
		if ((cp < '0' || cp > '9') && (cp < 'A' || cp > 'F'))
		{
			if (cp >= 'a')
			{
				return cp <= 'f';
			}
			return false;
		}
		return true;
	}

	public static bool IsOctalDigit(char cp)
	{
		if (cp >= '0')
		{
			return cp <= '7';
		}
		return false;
	}

	public static string FromCodePoint(int cp)
	{
		return char.ConvertFromUtf32(cp);
	}
}
